mapper.select:
select * from (
	select * from Video left join videocheck on Video.id = videocheck.videoID
	where Video.playlistID=#{playlistID} 
)as a
union
select * from (
	select * from Video right join videocheck on Video.id = videocheck.videoID
	where Video.playlistID=#{playlistID} 
)as b
order by seq ASC
