mapper.update:
update videocheck
set lastTime=#{lastTime}, timer=#{timer}
where studentEmail=#{studentEmail} AND videoID=#{videoID}

update videocheck
set lastTime=#{lastTime}, timer=#{timer}, watched=#{watched}
where studentEmail=#{studentEmail} AND videoID=#{videoID}

mapper.select:
select *
from videocheck
where studentEmail=#{studentEmail} AND videoID=#{videoID}
